import { observer, inject } from "mobx-react";
import React, { Component } from "react";

@inject(pos => ({
  mylat : pos.mylat,
  mylng : pos.mylng
}))
@observer
class Kakaomap extends Component {

  map = (a,b) => {
    const { pos } = this.props;
    console.log('xl',document.getElementById('xl').value);
    let mapContainer = document.getElementById("map"), // 지도를 표시할 div
      mapOption = {
        center: new kakao.maps.LatLng(a,b), // 지도의 중심좌표
        level: 3 // 지도의 확대 레벨
      };

    let map = new kakao.maps.Map(mapContainer, mapOption);
  };

  componentDidMount() {
    const { mylat, mylng} = this.props;
    console.log('didmount pos ', mylat, mylng)
    this.map(mylat, mylng);

  }

  render() {
    const { pos } = this.props;
    console.log('render pos', pos);
    return (
      <>
      <div id="map">1</div>
      <input name="xl" id="xl" value={pos.mylat} required/>
      <input name="yl" id="yl" value={pos.mylng} required/>
      <button onClick={pos.test1}>새로고침</button>
      </>
    )
  }
}

export default Kakaomap;
